<script setup>
import PieChart from '../components/SessionTwoPieChart.vue'
import { reactive, getCurrentInstance, onMounted } from 'vue'
import bus from './public.js'
// import PanelGroup from '../components/PanelGroup.vue'
const { proxy } = getCurrentInstance()
const { ipcRenderer } = require('electron')

const carddata = reactive({
  avg_money: localStorage.getItem('avg_money'),
  avg_count: localStorage.getItem('avg_count'),
  user_count1: 0,
  user_count2: 0,
  user_count3: 0,
  user_count4: 0,
})

// 定义表格中的数据
const tableData = reactive({
  Tcolumns: [],
  data: [],
  filePath: localStorage.getItem('filePath'),
 
})

var fileName = localStorage.getItem('session2FilePath')
proxy.$axios
  // .post('http://cupback.fcode.ltd/getFileContent')
  .post('http://127.0.0.1:5000/fileCategory', {
    filename: fileName,
    avg_money: carddata.avg_money,
    avg_count: carddata.avg_count,
    u_count: localStorage.getItem("u_count"),
    u_money: localStorage.getItem("u_money")
  })
  .then(function (res) {
    if (res.data.code === 200) {
      tableData.Tcolumns = res.data.columns
      tableData.data = res.data.data
      localStorage.setItem('Session2FileName', res.data.filename)
      localStorage.setItem('session2Columns', res.data.columns)
      localStorage.setItem('session2SavePath', res.data.savePath)
      tableData.user_count1 = res.data.user_count[0]
      tableData.user_count2 = res.data.user_count[1]
      tableData.user_count3 = res.data.user_count[2]
      tableData.user_count4 = res.data.user_count[3]
      bus.emit('piechart', res.data.user_count)
    }
  })
// 点击下载，传参下载的链接即可
const downloadSession2 = () => {
  var url = 'http://127.0.0.1:5000/download1?filepath=' + localStorage.getItem('session2SavePath')
  ipcRenderer.send('download', url)
}


</script>

